import { List, message, Avatar, Spin } from 'antd';
import reqwest from 'reqwest';
import React from 'react'

import InfiniteScroll from 'react-infinite-scroller';

const fakeDataUrl = 'https://randomuser.me/api/?results=5&inc=name,gender,email,nat&noinfo';

class AntdComment extends React.Component {
  state = {
    // data: [],
    loading: false,
    hasMore: true,
  };

//   componentDidMount() {
//     this.fetchData(res => {
//       this.setState({
//         data: res.results,
//       });
//     });
//   }

//   fetchData = callback => {
//     reqwest({
//       url: fakeDataUrl,
//       type: 'json',
//       method: 'get',
//       contentType: 'application/json',
//       success: res => {
//         callback(res);
//       },
//     });
//   };

  handleInfiniteOnLoad = () => {
    let { data } = this.state;
    this.setState({
      loading: true,
    });
    if (data.length > 14) {
      message.warning('Infinite List loaded all');
      this.setState({
        hasMore: false,
        loading: false,
      });
      return;
    }
    this.fetchData(res => {
      data = data.concat(res.results);
      this.setState({
        data,
        loading: false,
      });
    });
  };

  render() {
    const {id, review,} = this.props.review

    return (
      <div className="demo-infinite-container" style={{Color:'white'}}>
        <InfiniteScroll
          initialLoad={false}
          pageStart={0}
          loadMore={this.handleInfiniteOnLoad}
          hasMore={!this.state.loading && this.state.hasMore}
          useWindow={false}
        >
          <List
            dataSource={this.props.review}
            size="large"
            renderItem={item => (
              <List.Item key={id}>
                <List.Item.Meta
                  avatar={
                    <Avatar src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png" />
                  }
                  title={<a href="https://ant.design">{item.name.last}</a>}
                  description={review}
                  style={{backgroundColor:'white',height:'50px'}}
                />
                <div style={{backgroundColor:'white', height:'50px',width:'60px'}} >Content</div>
              </List.Item>
            )}
          >
            {this.state.loading && this.state.hasMore && (
              <div className="demo-loading-container">
                <Spin />
              </div>
            )}
          </List>
        </InfiniteScroll>
      </div>
    );
  }
}

export default AntdComment
