import { List, message, Avatar, Spin } from 'antd';
import React from 'react'
import { EditOutlined } from '@ant-design/icons';



class AntdComment extends React.Component {
    render() {
        const data = this.props.comments
        // console.log(data, 'helloworld')
        return (

            <List
                itemLayout="horizontal"
                dataSource={data}
                renderItem={item => (
                    <List.Item>
                        <List.Item.Meta
                            title={<a href="https://ant.design">{item.username}</a>} // เปลี่ยนเป็น ชื่อ user
                            // description={item.review} เปลี่ยนเป็น รหัสการจอง
                            style={{ backgroundColor: 'rgb(190, 29, 55)', height: '50px', width: '15%', display: 'flex', justifyContent: 'center', alignItems: 'center' }}
                        />
                        <div style={{ backgroundColor: 'white', height: '50px', width: '77%', display: 'flex', justifyContent: 'flex-start', alignItems: 'center', paddingLeft: '3%' }}>{item.review}</div>
                        <div style={{ backgroundColor: 'rgb(190, 29, 55)', color: 'white', height: '50px', width: '8%', display: 'flex', justifyContent: 'center', alignItems: 'center', fontWeight: 'Bold', flexWrap: 'wrap' }} ><span style={{ fontSize: '1rem', paddingLeft: '0.3rem' }}>{item.score}</span></div>
                        {/* <div style={{ backgroundColor: 'white', height: '50px', width: '60px', display:'flex',justifyContent:'center',alignItems:'center' }}><EditOutlined key="edit"/></div> */}
                    </List.Item>
                )}
            />
        );
    }
}


export default AntdComment
