import React, { useEffect, useState } from 'react';
import axios from 'axios'
import { Layout, Form, Input, Button, Rate, Modal, InputNumber, DatePicker, Upload } from 'antd';
import Navbar from '../../component/Navbar/Navbar'
import Footer from '../../component/Footer/Footer'
import { UploadOutlined } from '@ant-design/icons';
import AntdComment from '../../component/Comment/antdComment'
import './standeeProfile.css'
import data from '../../data/data'
import _ from 'lodash'
import {useParams} from 'react-router-dom'

const { Content } = Layout;
const contentStyle = {
    paddingTop: '10vh',
    backgroundColor: '#0D0311',
    fontFamily: 'Montserrat',
    height:'100%',
    paddingBottom:'20vh'
}

const layout = {
    labelCol: {
        span: 24,
    },
    wrapperCol: {
        span: 24,
    },
};

const validateMessages = {
    required: '${label} is required!',
    types: {
        email: '${label} is not a valid email!',
        number: '${label} is not a valid number!',
    },
    number: {
        range: '${label} must be between ${min} and ${max}',
    },
};

const StandeeProfile = (props) => {

    const url = window.location.href.split('/')
    const length = url.length
    const username = url[length-1]
    const users = data.users
    const onFinish = (values) => {
        console.log(values);
    };

    const [isModalVisible1, setIsModalVisible1] = useState(false);
    const [isModalVisible2, setIsModalVisible2] = useState(false);
    const [inputReview, setInputReview] = useState('')
    const [inputScore, setInputScore] = useState(0)
    const [avgScore, setAvgScore] = useState(0)
    const [profile, setProfile] = useState()

    useEffect(() => {
        getUserByUsername()
        console.log(username)
    }, [])

    const getUserByUsername = async () => {
        const httpResponse = await axios.get(`http://localhost:5000/users/${username}`)
        console.log(httpResponse.data)
        setProfile(httpResponse.data)
        console.log(profile)
    }

    const fetchComments = async () => {
        const httpResponse = await axios.get('http://localhost:5000/comments')
        setComments(httpResponse.data)
    }

    const calScore = async() => {
        const httpResponse = await axios.get('http://localhost:5000/comments')
        let sum = 0
        let n = 0
        let avg
        httpResponse.data.map( x => {
            sum = sum + x.score
            n = httpResponse.data.length
            avg = (sum/n).toFixed(1)
        })
        setAvgScore(avg)
        // console.log(avgScore)
    }
    calScore()

    const addComment = async () => {
        await axios.post('http://localhost:5000/comments', {review: inputReview, score: inputScore})
        // await axios.post('http://localhost:5000/comments', {score: inputScore})
        fetchComments()
        calScore()
        console.log(avgScore)
    }

    useEffect(() => {
        fetchComments();
    },[])

    const showModal1 = () => {
        setIsModalVisible1(true);
    };

    const handleOk1 = () => {
        setIsModalVisible1(false);
    };

    const handleCancel1 = () => {
        setIsModalVisible1(false);
    };

    const showModal2 = () => {
        setIsModalVisible2(true);
    };

    const handleOk2 = () => {
        setIsModalVisible2(false);
    };

    const handleCancel2 = () => {
        setIsModalVisible2(false);
    };

    const styleContainer = {
        width: '100%',
        height: '100%',
        margin: '0 0 0 auto'
    }

    const [comments, setComments] = useState([])

    return (
        <Layout className="layout" style={{backgroundColor:'#0D0311', paddingBottom:'10%'}}>
            <Navbar setRole={props.setRole}/>
            <Content style={contentStyle} >
                <div className='profile-container'>
                    <div className='profile-section'>
                        <div className='img-profile'>
                            <img alt={users[0]._id} src={users[0].image} style={{ width: '230px' }}></img>
                        </div>
                        <div className='info-profile'>
                            <h3 style={{ color: 'white' }}><span style={{fontWeight:'bolder', fontSize:'1.3rem'}}>{name}</span> | <span style={{fontWeight:'bolder', fontSize:'0.8rem'}}>Average Score : <span style={{color:'#90EE90', fontSize:'1rem'}}>{avgScore}</span></span> </h3> <hr></hr>
                            {/* <h4 style={{ color: 'white' }}>{users[0].description}</h4> */}
                            <h4 style={{ color: '#BE1D37' }}>{hashtag}</h4>
                            <h3 style={{ color: 'white' }}>Price rate</h3> <hr></hr>
                            {users[0].pricerate}
                            <br></br>
                            <Button type="primary" onClick={showModal1} size='large' style={{ marginRight: '3%', marginTop: '4%', backgroundColor: '#BE1D37', border: '#BE1D37',width:'200px', marginBottom:'3%' }}>BE MY STANDEE</Button>
                            <Modal title='Booking Order' visible={isModalVisible1} onOk={handleOk1} onCancel={handleCancel1} footer={null} width={350}>
                                <div style={{ ...styleContainer }}>
                                    <Form {...layout} name="nest-messages" layout='vertical' onFinish={onFinish} validateMessages={validateMessages} style={{ width: '100%' }}>
                                        <Form.Item
                                            label='Restaurant'
                                            name={['user', 'name']}
                                            style={{ margin: '0 0 10px 0' }}
                                        >
                                            <Input placeholder="RESTAURANT's NAME" />
                                        </Form.Item>
                                        <Form.Item
                                            label='No. of People'
                                            name={['user', 'Number']}
                                            style={{ margin: '0 0 10px 0' }}
                                        >
                                            <InputNumber placeholder='Number' style={{ width: '100%' }} />
                                        </Form.Item>
                                        <Form.Item label="Date"
                                            style={{ margin: '0 0 10px 0' }}>
                                            <DatePicker showTime format="YYYY-MM-DD HH:mm:ss" style={{ width: '100%' }} />
                                        </Form.Item>
                                        <Form.Item
                                            name={['user', 'description']}
