import React, {useEffect} from 'react'
import { Button, List } from 'antd';
import { UserOutlined } from '@ant-design/icons';
import axios from 'axios'
import localStorageService from '../../services/localStorageService'
import jwtDecode from 'jwt-decode'



const BookingCardAntd = () => {

    let username = ''
    const getUsername = async () => {
        const token = await localStorageService.getToken();
        if (token) {
            const user = jwtDecode(token)
            // console.log(user.name)
            username = user.name
        }
    }

    const myBooking = []
    const fetchReservation = async () => {
        const httpResponse = await axios.get('http://localhost:5000/reservation')
        const result = await axios.get(`http://localhost:5000/users/${username}`)
        httpResponse.data.map( x => {
            if (x.user_id === result.data.id){
                myBooking.push(x)
                // console.log(myBooking)
            }
        })
    }

    const getAlldata = async () => {
        await getUsername()
        await fetchReservation()
    }

    useEffect(() => {
        getAlldata()
    },[])
    return (
        <List
            itemLayout="vertical"
            size="large"
            style={{backgroundColor:'white', width:'100%'}}
            dataSource={myBooking}
            renderItem={item => (
                <List.Item
                    key={item.id}
                    extra={
                        <div>
                            <div><UserOutlined/> {item.number} || {item.date}</div></div>
                    }
                >
                    <List.Item.Meta
                        title={item.restaurant}
                        description={item.description}
                    />
                    {item.fromUsername}
                    {item.toId}
                </List.Item>
            )}
        />
    )
}

export default BookingCardAntd
