import React, { useEffect, useState } from 'react';
import axios from 'axios'
import { Layout, Row, Col, Tabs} from 'antd';
import Navbar from '../../component/Navbar/Navbar'
import Footer from '../../component/Footer/Footer'
import _ from 'lodash'
import localStorageService from '../../services/localStorageService'
import jwtDecode from 'jwt-decode'
import BookingCard2 from '../../component/BookingCard/BookingCardAntd'
import BookingCard22 from '../../component/BookingCard/BookingCardAntd2'

const { TabPane } = Tabs;
const { Content } = Layout;
const contentStyle = {
    width:'100%',
    paddingTop: '10vh',
    fontFamily: 'Montserrat',
    height:'100%',
    paddingBottom:'10vh',
    marginBottom:'40vh',
    display:'flex',
    flexDirection:'column'
}
function callback(key) {
    console.log(key);
}


const Booking = (props) => {

    let username = ''
    const getUsername = async () => {
        const token = await localStorageService.getToken();
        if (token) {
            const user = jwtDecode(token)
            // console.log(user.name)
            username = user.name
        }
    }

    const [myBooking] = []
    const incomeBooking = []
    const fetchReservation = async () => {
        const httpResponse = await axios.get('http://localhost:5000/reservation')
        const result = await axios.get(`http://localhost:5000/users/${username}`)
        httpResponse.data.map( x => {
            if (x.user_id === result.data.id){
                myBooking.push(x)
                // console.log(myBooking)
            }
            if (x.toId === result.data.id){
                incomeBooking.push(x)
                console.log(incomeBooking)
            }
        })
    }

    const getAlldata = async () => {
        await getUsername()
        await fetchReservation()
    }

    useEffect(() => {
        getAlldata()
    },[])
    return (
        <Layout className="layout" style={{backgroundColor:'#0D0311', paddingBottom:'10%'}}>
            <Navbar setRole={props.setRole}/>
            <Content style={contentStyle} >
                <Tabs onChange={callback} type="card">
                    <TabPane tab={<div style={{color:'black'}}>MY BOOKING</div>} key="1">
                        <Row>
                            <Col span={10} offset={3}><BookingCard2 myBooking={myBooking}/></Col>
                        </Row>
                    </TabPane>
                    <TabPane tab={<div style={{color:'black'}}>INCOMING BOOKING</div>} key="2">
                        <Row>
                            <Col span={10} offset={3}><BookingCard22 incomeBooking={incomeBooking}/></Col>
                        </Row>
                    </TabPane>
                </Tabs>
            </Content>
            <Footer />
        </Layout>
    )
}




export default Booking;
