import React from 'react'
import { Form, Input, Button, Checkbox, notification, Result } from 'antd';
import './loginform.css'
import axios from '../../../config/axios'
import localStorageService from '../../../services/localStorageService'

// const layout = {
//     labelCol: {
//         span: 12,
//     },
//     wrapperCol: {
//         span: 12,
//     },
// };
// const tailLayout = {
//     wrapperCol: {
//         offset: 20,
//         span: 4,
//     },
// };

const LoginForm = (props) => {
    const onFinish = (values) => {
        const body = {
            username :values.username, 
            password :values.password
        }
        axios.post('users/login', body)
        .then(res => {
            localStorageService.setToken(res.data.token)
            props.setRole('user')
        })
        .catch(err => { 
            notification.error({
                message: 'เข้าสู่ระบบล้มเหลว'
            })
        })
    };

    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };

    return (
        <Form
            {...layout}
            name="basic"
            initialValues={{
                remember: true,
            }}
            onFinish={onFinish}
            onFinishFailed={onFinishFailed}
            style={{backgroundColor:'#DCC8D5', width:'100%', paddingRight:'10vw', borderRadius:'10px',display:'flex', flexDirection:'column', alignItems:'center'}}
        >
            <h3 style={{textAlign:'center', fontFamily:'Montserrat', fontSize:'1.5rem', marginTop:'1rem', fontWeight:'bolder',width:'250px'}}>SIGN IN - เข้าสู่ระบบ</h3>

            <Form.Item
                label="Username"
                name="username"
                // style={{width:'250px',}}
                rules={[
                    {
                        required: true,
                        message: 'Please input your username!',
                    },
                ]}

            >
                <Input  style={{borderRadius:'3px',width:'230px'}}/>
            </Form.Item>

            <Form.Item
                label="Password"
                name="password"
                rules={[
                    {
                        required: true,
                        message: 'Please input your password!',
                    },
                ]}
            >
                <Input.Password style={{ width:'230px', borderRadius:'3px',}}/>
            </Form.Item>

            <Form.Item {...tailLayout}>
                <Button type="primary" htmlType="submit">
                    Submit
                </Button>
            </Form.Item>
        </Form>
    );
};


export default LoginForm
