import React from 'react'
import './booking.css'
import { Form, Input, InputNumber, Button, Upload } from 'antd';

const layout = {
    labelCol: {
        span: 8,
    },
    wrapperCol: {
        span: 16,
    },
    labelAlign: 'left'
};
/* eslint-disable no-template-curly-in-string */

const validateMessages = {
    required: '${label} is required!',
    types: {
        email: '${label} is not a valid email!',
        number: '${label} is not a valid number!',
    },
    number: {
        range: '${label} must be between ${min} and ${max}',
    },
};

const BookingForm = () => {
    const onFinish = (values) => {
        console.log(values);
    };

    return (
        <div className='container'>
            <h1>Booking Order</h1>
            <div className="form-container">
                <Form {...layout} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages} style={{ width: '100%' }}>
                    <Form.Item
                        label='Restaurant'
                        name={['user', 'name']}
                        rules={[
                            {
                                required: true,
                            },
                        ]}
                    >
                        <Input placeholder="RESTAURANT's NAME" />
                    </Form.Item>
                    <Form.Item
                        label='No. of People'
                        name={['user', 'age']}
                        rules={[
                            {
                                type: 'number',
                                min: 0,
                                max: 10,
                                required: true
                            },
                        ]}
                    >
                        <InputNumber placeholder='Number' />
                    </Form.Item>
                    <Form.Item
                        name={['user', 'description']}
                        label="Description"
                        labelAlign='right'
                    >
                        <Input.TextArea />
                    </Form.Item>
                    <Form.Item label="Pay Slip" rules={[
                            {
                                required: true,
                            },
                        ]}
                            labelAlign='right'>
                        <Upload name="logo" action="/upload.do" listType="picture">
                            <Button>
                                Click to upload
                            </Button>
                        </Upload>
                    </Form.Item>
                    <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
                        <Button type="primary" htmlType="submit">
                            Submit
                        </Button>
                    </Form.Item>
                </Form>
            </div>
        </div>
    )
}

export default BookingForm
