import React from 'react'
import { Form, Input, Button, Checkbox, notification, Result } from 'antd';
import './loginform.css'
import axios from '../../../config/axios'
import localStorageService from '../../../services/localStorageService'

const layout = {
    labelCol: {
        span: 12,
    },
    wrapperCol: {
        span: 12,
    },
};
const tailLayout = {
    wrapperCol: {
        offset: 16,
        span: 8,
    },
};

const LoginForm = (props) => {
    const onFinish = (values) => {
        const body = {
            username: values.username,
            password: values.password
        }
        axios.post('users/login', body)
            .then(res => {
                localStorageService.setToken(res.data.token)
                props.setRole('user')
            })
            .catch(err => {
                notification.error({
                    message: 'เข้าสู่ระบบล้มเหลว'
                })
            })
    };

    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };

    return (
        <div style={{width:''}}>
            <h3 style={{ textAlign: 'center', fontFamily: 'Montserrat', fontSize: '1.5rem', marginTop: '1rem', fontWeight: 'bolder', }}>SIGN IN - เข้าสู่ระบบ</h3>
            <Form
                {...layout}
                name="basic"
                initialValues={{
                    remember: true,
                }}
                onFinish={onFinish}
                onFinishFailed={onFinishFailed}
                style={{ backgroundColor: '#DCC8D5', width: '30%', paddingRight: '10vw', borderRadius: '10px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}
            >
                <h3 style={{ textAlign: 'center', fontFamily: 'Montserrat', fontSize: '1.5rem', marginTop: '1rem', fontWeight: 'bolder', }}>SIGN IN - เข้าสู่ระบบ</h3>

                <Form.Item
                    label="Username"
                    name="username"
                    rules={[
                        {
                            required: true,
                            message: 'Please input your username!',
                        },
                    ]}

                >
                    <Input style={{ width: '150%', borderRadius: '3px', }} />
                </Form.Item>

                <Form.Item
                    label="Password"
                    name="password"
                    rules={[
                        {
                            required: true,
                            message: 'Please input your password!',
                        },
                    ]}
                >
                    <Input.Password style={{ width: '150%', borderRadius: '3px', }} />
                </Form.Item>

                <Form.Item {...tailLayout}>
                    <Button type="primary" htmlType="submit">
                        Submit
                </Button>
                </Form.Item>
            </Form>
        </div>

    );
};


export default LoginForm
