import React, { useEffect, useState } from 'react'
import { Button, List, Descriptions } from 'antd';
import { UserOutlined } from '@ant-design/icons';
import axios from 'axios'
import localStorageService from '../../services/localStorageService'
import jwtDecode from 'jwt-decode'



const BookingCardAntd = (props) => {

    let username = ''
    const getUsername = async () => {
        const token = await localStorageService.getToken();
        if (token) {
            const user = jwtDecode(token)
            // console.log(user.name)
            username = user.name
        }
    }
    const [Booking, setBooking] = useState([])
    const fetchReservation = async () => {
        const myBooking = []
        let n = 0
        const httpResponse = await axios.get('http://localhost:5000/reservation')
        const result = await axios.get(`http://localhost:5000/users/${username}`)
        httpResponse.data.map(x => {
            if (x.user_id === result.data.id) {
                myBooking.push(x)
                n += 1
            }
        })
        setBooking(myBooking)
    }

    const getAlldata = async () => {
        await getUsername()
        await fetchReservation()
    }

    const completeBooking = async (id) => {
        const newStatus = 'COMPLETED'
        await axios.put(`/reservation/${id}`, { option: newStatus })
        await getAlldata()
        console.log(id)
    }

    useEffect(() => {
        getAlldata()
    }, [])
    return (
        <List
            itemLayout="vertical"
            size="large"
            style={{ width: '100%' }}
            dataSource={Booking}
            renderItem={item => (
                <List.Item
                    key={item.id}
                >
                    <div className='container'>
                        <div className='date'>
                            <h1>07<br></br>MAY<br></br>2021</h1>
                        </div>
                        <Descriptions
                            title={item.restaurant}
                            bordered
                            column={{ xxl: 4, xl: 3, lg: 3, md: 3, sm: 2, xs: 1 }}
                            size='small'
                            layout='horizontal'
                            className='descriptions'
                            style={{ backgroundColor: 'white' }}
                        >
                            <Descriptions.Item label="Person">{item.number}</Descriptions.Item>
                            <br></br>
                            <Descriptions.Item label="Date">{item.date}</Descriptions.Item>
                            <Descriptions.Item label="Description">{item.description}</Descriptions.Item>
                        </Descriptions>
                        <div className='response'>
                            <div style={{ fontSize: '1.5rem', fontWeight: 'regular', paddingRight: '1rem' }}>Bukayo Saka</div>
                            <div style={{ fontSize: '1.2rem', fontWeight: 'regular', paddingRight: '1rem' }}>STATUS : {item.option}</div>
                            <div style={{ textAlign: 'center' }}><Button type="primary"><span style={{ fontSize: '1rem' }}>CANCEL ORDER</span></Button></div>
                        </div>
                    </div>
                </List.Item>
            )}
        />
    )
}

export default BookingCardAntd
