import { Avatar, Comment, Rate } from 'antd';
import React, {useEffect, useState} from 'react'
import { EditOutlined } from '@ant-design/icons';
import axios from 'axios'
import data from '../../data/data'
import pic from './1.jpeg'

const commentStyle = {
    backgroundColor: '#F5F1E3',
    padding: '5px 5px 5px 20px',
    margin: '10px 0 10px 0',
    borderRadius: '10px',
    fontFamily: 'Montserrat'
    // color: 'white'
}

const AntdComment = (props) => {
    const [picId, setPicId] = useState({})
    const users = data.users
    const reviewData = props.comments
    // console.log(reviewData[0])
    let userPic

    const checkPic = () => {
        if (reviewData[0] === undefined) {
            userPic = pic
            console.log(userPic)
        } else {
            userPic = users[reviewData[0].user_id - 1].image
            console.log(userPic)
        }
    }

    useEffect(() => {
        checkPic()

    }, [reviewData])

    return (
            reviewData.map(item => (
                <Comment
                    avatar={
                        <div>
                            <Avatar
                                src={'/user_image/3.jpeg'}
                                alt={users[7].id}
                            />
                        </div>
                    }
                    author={<span style={{ fontSize: '1.3rem' }}>{item.from} | <Rate disabled defaultValue={item.score} /></span>}
                    style={commentStyle}
                    content={
                        <p style={{color:'black'}}>
                            {item.review}
                        </p>
                    }
                />
            ))
    )
}
export default AntdComment
