import React, {useEffect, useState} from 'react';
import axios from 'axios'
import Navbar from '../../component/Navbar/Navbar'
import Footer from '../../component/Footer/Footer'
import { Input, Space, Layout, Button } from 'antd';
import Usercard from '../../component/Usercard/Usercard'
import data from '../../data/data'

const { Content } = Layout;
const contentStyle = {
    height: '100%',
    width: '100%',
    backgroundColor: '#0D0311',
    display: 'flex',
    justifyContent: 'center',
    flexDirection: 'column',
    paddingTop: '35vh',
    alignItems: 'center'
}

const divStyle1 = {
    // border:'2px solid green',
    width: '90vw',
    height: '70vh',
    color: 'red',
    textAlign: 'center'
}

const divStyle2 = {
    // border:'2px solid lightgreen',
    width: '90vw',
    display: 'flex',
    flexDirection: 'column',

}

const divStyle3 = {
    width: '100%',
    display: 'flex',
    justifyContent: 'space-around',
    flexWrap: 'wrap',
    alignItems: 'center',

    // border:'2px solid lightgreen',
}

const divStyle4 = {
    color: 'white',
    fontFamily: 'Montserrat',
    fontWeight: 'Bolder',
    fontSize: '2rem',
    marginBottom: '2rem'
}

const goToResult = () => {
    window.scrollTo({
        top: window.innerHeight - 50,
        behavior: 'smooth'
    })
}

const { Search } = Input;

const onSearch = value => console.log(value);

const Find = () => {
    const [users, setUsers] = useState([]);
    useEffect(() => {
        const fetchData = async () => {
            const {data} = await axios.get('/api/users')
            setUsers(data)
        };
        fetchData();
    }, [])
    con
    console.log(users)

    return (
        <Layout>
            <Navbar />
            <Content style={contentStyle}>
                <div style={divStyle1}>
                    <Space direction="vertical" style={{ textAlign: 'center' }}>
                        <Search
                            placeholder="ENTER NAME/HASHTAG TO FIND YOUR STANDEE"
                            size="large"
                            onSearch={goToResult}
                            style={{ fontFamily: 'Montserrat', fontWeight: 'Bold', width: '35vw' }}
                        />
                        <div style={{ fontFamily: 'Montserrat', fontWeight: 'Bold', color: '#F5F1E3', fontSize: '1rem' }}>or</div>
                        <Button onClick={goToResult} type="primary" shape='round' style={{ height: '3rem', fontFamily: 'Montserrat', fontWeight: 'Bold', color: '#5C082A', backgroundColor: '#F5F1E3', }}>TELL SOMEONE AROUND THERE</Button>
                    </Space>
                </div>
                <div style={divStyle2}>
                    <div style={divStyle4} className='result'>RESULT</div>
                    <div style={divStyle3}>
                        {users.map(user => (
                            <Usercard key={user._id} user={user} />
                        ))}
                    </div>
                </div>
            </Content>
            <Footer />
        </Layout>
    )
}





export default Find
