import { List, message, Avatar, Spin, Comment, Tooltip, Rate } from 'antd';
import React, {useEffect, useState} from 'react'
import { EditOutlined } from '@ant-design/icons';
import moment from 'moment';
import Item from 'antd/lib/list/Item';
import axios from 'axios'

const commentStyle = {
    backgroundColor: '#F5F1E3',
    padding: '5px 5px 5px 20px',
    margin: '10px 0 10px 0',
    borderRadius: '10px',
    fontFamily: 'Montserrat'
    // color: 'white'
}

const AntdComment = (props) => {
    const data = props.comments
    const [allUser, setAllUser] = useState()
    const getUsers = async () => {
        const result = await axios.get(`http://localhost:5000/users/`)
        setAllUser(result.data)
        console.log(data)
    }

    useEffect(() => {
        getUsers()
    }, [])

    return (
            data.map(item => (
                <Comment
                    avatar={
                        <div>
                            <Avatar
                                src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
                                alt="Han Solo"
                            />
                        </div>
                    }
                    author={<span style={{ fontSize: '1.3rem' }}>{item.user_id} | <Rate disabled defaultValue={item.score} /></span>}
                    style={commentStyle}
                    content={
                        <p style={{color:'black'}}>
                            {item.review}
                        </p>
                    }
                />
            ))
    )
}
export default AntdComment
