import React from 'react'
import './booking.css'
import { Form, Input, InputNumber, Button } from 'antd';

const layout = {
    labelCol: {
        span: 8,
    },
    wrapperCol: {
        span: 16,
    },
    labelAlign: 'left'
};
/* eslint-disable no-template-curly-in-string */

const validateMessages = {
    required: '${label} is required!',
    types: {
        email: '${label} is not a valid email!',
        number: '${label} is not a valid number!',
    },
    number: {
        range: '${label} must be between ${min} and ${max}',
    },
};

const BookingForm = () => {
    const onFinish = (values) => {
        console.log(values);
    };
    const { getFieldDecorator } = this.props.form;
    handleSubmit = e => {
        e.preventDefault();
        this.props.form.validateFields((err, values) => {
          if (!err) {
            console.log('Received values of form: ', values);
          }
        });
      };

      normFile = e => {
        console.log('Upload event:', e);
        if (Array.isArray(e)) {
          return e;
        }
        return e && e.fileList;
      };

    return (
        <div className='container'>
            <h1>Booking Order</h1>
            <div className="form-container">
                <Form {...layout} name="nest-messages" onFinish={onFinish} validateMessages={validateMessages} style={{ width: '100%' }}>
                    <Form.Item
                        label='Restaurant'
                        name={['user', 'name']}
                        rules={[
                            {
                                required: true,
                            },
                        ]}
                    >
                        <Input placeholder="RESTAURANT's NAME" />
                    </Form.Item>
                    <Form.Item
                        label='No. of People'
                        name={['user', 'age']}
                        rules={[
                            {
                                type: 'number',
                                min: 0,
                                max: 10,
                                required: true
                            },
                        ]}
                    >
                        <InputNumber placeholder='Number' />
                    </Form.Item>
                    <Form.Item
                        name={['user', 'introduction']}
                        label="Description"
                        labelAlign='right'
                    >
                        <Input.TextArea />
                    </Form.Item>
                    <Form.Item label="Upload" extra="longgggggggggggggggggggggggggggggggggg">
                        {getFieldDecorator('upload', {
                            valuePropName: 'fileList',
                            getValueFromEvent: this.normFile,
                        })(
                            <Upload name="logo" action="/upload.do" listType="picture">
                                <Button>
                                    <Icon type="upload" /> Click to upload
                                </Button>
                            </Upload>,
                        )}
                    </Form.Item>
                    <Form.Item wrapperCol={{ ...layout.wrapperCol, offset: 8 }}>
                        <Button type="primary" htmlType="submit">
                            Submit
                        </Button>
                    </Form.Item>
                </Form>
            </div>
        </div>
    )
}

export default BookingForm
