import moment from 'moment';
import Item from 'antd/lib/list/Item';

const commentStyle = {
    backgroundColor: 'rgb(190, 29, 55)',
    padding: '5px 5px 5px 20px',
    margin: '10px 0 10px 0',
    // color: 'white'
}

class AntdComment extends React.Component {
    render() {
        const data = this.props.comments
        console.log(data)
        return (
            <List
                itemLayout="horizontal"
                dataSource={data}
                renderItem={item => (
                    <Comment
                        avatar={
                            <Avatar
                                src="https://zos.alipayobjects.com/rmsportal/ODTLcjxAfvqbxHnVXCYX.png"
                                alt="Han Solo"
                            />
                        }
                        author={item.id}
                        style={commentStyle}
                        content={
                            <p>
                                {item.review}
                            </p>
                        }
                    // datetime={
