                    //         style={{ backgroundColor: 'rgb(190, 29, 55, 0.1)', height: '90px', width: '15%', display: 'flex', justifyContent: 'center', alignItems: 'center' }}
                    //     />
                    //     <div style={{ backgroundColor: 'white', height: '90px', width: '77%', display: 'flex', justifyContent: 'flex-start', alignItems: 'center', paddingLeft: '3%' }}>{item.review}</div>
                    //     <div style={{ backgroundColor: 'rgb(190, 29, 55)', color: 'white', height: '90px', width: '8%', display: 'flex', justifyContent: 'center', alignItems: 'center', fontWeight: 'Bold', flexWrap: 'wrap' }} ><span style={{ fontSize: '1rem', paddingLeft: '0.3rem' }}>{item.score}</span></div>
                    // </List.Item>
                )
                }
            />
        );
    }
}


export default AntdComment
