import React from 'react'
import { EditOutlined } from '@ant-design/icons';
import moment from 'moment';
import Item from 'antd/lib/list/Item';

const commentStyle = {
    backgroundColor: 'rgb(190, 29, 55)',
    padding: '5px 5px 5px 20px',
    margin: '10px 0 10px'
}

class AntdComment extends React.Component {
    render() {
        const data = this.props.comments
        // console.log(data, 'helloworld')
        return (
            <List
                itemLayout="horizontal"
                dataSource={data}
                renderItem={item => (
                    <Comment
                        author={<a>Han Solo</a>}
                        style={commentStyle}
                        content={
                            <p>
                                {item.review}
