import React, { useEffect, useState } from 'react';
import axios from 'axios'
import { Layout, Form, Input, Button, Rate, Modal } from 'antd';
import Navbar from '../../component/Navbar/Navbar'
import Footer from '../../component/Footer/Footer'
import { Link } from 'react-router-dom'
import './Profile.css'
import data from '../../data/data'

const { Content } = Layout;
const contentStyle = {
    paddingTop: '10vh',
    width: '100%',
    backgroundColor: '#0D0311',
    fontFamily: 'Montserrat',
}

const layout = {
    labelCol: {
        span: 24,
    },
    wrapperCol: {
        span: 24,
    },
};

const validateMessages = {
    required: '${label} is required!',
    types: {
        email: '${label} is not a valid email!',
        number: '${label} is not a valid number!',
    },
    number: {
        range: '${label} must be between ${min} and ${max}',
    },
};

const Profile = () => {
    const users = data.users
    const onFinish = (values) => {
        console.log(values);
    };

    const [isModalVisible, setIsModalVisible] = useState(false);

    const showModal = () => {
        setIsModalVisible(true);
    };

    const handleOk = () => {
        setIsModalVisible(false);
    };

    const handleCancel = () => {
        setIsModalVisible(false);
    };

    const styleContainer = {
        width: '100%',
        height: '100%',
        margin: '0 0 0 auto'
    }


    return (
        <Layout className="layout" style={{ height: '100%' }}>
            <Navbar />
            <Content style={contentStyle} >
                <div className='profile-container'>
                    <div className='profile-section'>
                        <div className='img-profile'>
                            <img alt={users[0]._id} src={users[0].image} style={{ width: '230px' }}></img>
                        </div>
                        <div className='info-profile'>
                            <h3 style={{ color: 'white' }}>{users[0].name} </h3> <hr></hr>
                            <h4 style={{ color: 'white' }}>{users[0].description}</h4>
                            <h4 style={{ color: '#BE1D37' }}>{users[0].hashtag}</h4>
                            <h3 style={{ color: 'white' }}>Price rate</h3> <hr></hr>
                            {users[0].pricerate}
                            <br></br>
                            <Button type="primary" style={{ marginRight: '3%', marginTop: '4%', backgroundColor: '#BE1D37', border: '#BE1D37' }}>BE MY STANDEE</Button>

                            <Button type="primary" onClick={showModal} style={{ backgroundColor: '#BE1D37', border: '#BE1D37' }}>WRITE A REVIEW</Button>
                            <Modal title='Review Your Standee' visible={isModalVisible} onOk={handleOk} onCancel={handleCancel} footer={null} width={350} style={{ paddingRight: '0' }}>
                                <div style={{ ...styleContainer }}>
                                    <Form name="nest-messages" onFinish={onFinish} validateMessages={validateMessages} style={{ width: '100%' }}>
                                        <Form.Item
                                            {...layout}
                                            name={['user', 'description']}
                                            label="Tell me something?"
                                            style={{ margin: '0 0 20px 0', }}
                                        >
                                            <Input.TextArea style={{ height: '150px' }} />
                                        </Form.Item>
                                        <Form.Item label="Rate" layout='horizontal' style={{ margin: '0 0 20px 0' }}>
                                            <Rate />
                                        </Form.Item>
                                        <Form.Item wrapperCol={{ ...layout.wrapperCol }} style={{ margin: '0 0 10px 0' }}>
                                            <Button type="primary" htmlType="submit" block>
                                                Submit
                                </Button>
                                        </Form.Item>
                                    </Form>
                                </div>
                            </Modal>
                        </div>
                    </div>
                    <div className='comment-section'>
                        <h1 style={{ color: 'white', marginLeft: '0' }}>Review from User</h1>
                        <div className='comment-feed'>
                            <div className='comment'> //เอาไปทำเป็น component
                                <h3 style={{ color: 'black' }}>User's Name</h3>
                                <hr></hr>
                                <h5 style={{ color: 'black' }}>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua</h5>
                            </div>
                        </div>
                    </div>
                </div>
            </Content>
            <Footer />
        </Layout>
    )
}




export default Profile;
