import React, { useEffect, useState } from 'react';
import axios from 'axios'
import { Layout, Form, Input, Button, Rate, Modal, InputNumber, DatePicker, Upload } from 'antd';
import Navbar from '../../component/Navbar/Navbar'
import Footer from '../../component/Footer/Footer'
import { UploadOutlined } from '@ant-design/icons';
import AntdComment from '../../component/Comment/antdComment'
import './Profile.css'
import data from '../../data/data'
import Comment from '../../component/Comment/Comment'

const { Content } = Layout;
const contentStyle = {
    paddingTop: '10vh',
    backgroundColor: '#0D0311',
    fontFamily: 'Montserrat',
    height:'100vh'
}

const layout = {
    labelCol: {
        span: 24,
    },
    wrapperCol: {
        span: 24,
    },
};

const validateMessages = {
    required: '${label} is required!',
    types: {
        email: '${label} is not a valid email!',
        number: '${label} is not a valid number!',
    },
    number: {
        range: '${label} must be between ${min} and ${max}',
    },
};

const Profile = () => {
    const users = data.users
    const onFinish = (values) => {
        console.log(values);
    };

    const [isModalVisible1, setIsModalVisible1] = useState(false);
    const [isModalVisible2, setIsModalVisible2] = useState(false);


    const showModal1 = () => {
        setIsModalVisible1(true);
    };

    const handleOk1 = () => {
        setIsModalVisible1(false);
    };

    const handleCancel1 = () => {
        setIsModalVisible1(false);
    };

    const showModal2 = () => {
        setIsModalVisible2(true);
    };

    const handleOk2 = () => {
        setIsModalVisible2(false);
    };

    const handleCancel2 = () => {
        setIsModalVisible2(false);
    };

    const styleContainer = {
        width: '100%',
        height: '100%',
        margin: '0 0 0 auto'
    }


    return (
        <Layout className="layout" style={{backgroundColor:'red'}}>
            <Navbar />

            <div class="clearfix">
            <Content style={contentStyle} >
                <div className='profile-container'>
                    <div className='profile-section'>
                        <div className='img-profile'>
                            <img alt={users[0]._id} src={users[0].image} style={{ width: '230px' }}></img>
                        </div>
                        <div className='info-profile'>
                            <h3 style={{ color: 'white' }}>{users[0].name} </h3> <hr></hr>
                            <h4 style={{ color: 'white' }}>{users[0].description}</h4>
                            <h4 style={{ color: '#BE1D37' }}>{users[0].hashtag}</h4>
                            <h3 style={{ color: 'white' }}>Price rate</h3> <hr></hr>
                            {users[0].pricerate}
                            <br></br>
                            <Button type="primary" onClick={showModal1} size='large' style={{ marginRight: '3%', marginTop: '4%', backgroundColor: '#BE1D37', border: '#BE1D37',width:'200px', marginBottom:'3%' }}>BE MY STANDEE</Button>
                            <Modal title='Booking Order' visible={isModalVisible1} onOk={handleOk1} onCancel={handleCancel1} footer={null} width={350}>
                                <div style={{ ...styleContainer }}>
                                    <Form {...layout} name="nest-messages" layout='vertical' onFinish={onFinish} validateMessages={validateMessages} style={{ width: '100%' }}>
                                        <Form.Item
                                            label='Restaurant'
                                            name={['user', 'name']}
                                            style={{ margin: '0 0 10px 0' }}
                                        >
                                            <Input placeholder="RESTAURANT's NAME" />
                                        </Form.Item>
                                        <Form.Item
                                            label='No. of People'
                                            name={['user', 'Number']}
                                            style={{ margin: '0 0 10px 0' }}
                                        >
                                            <InputNumber placeholder='Number' style={{ width: '100%' }} />
                                        </Form.Item>
                                        <Form.Item label="Date"
                                            style={{ margin: '0 0 10px 0' }}>
                                            <DatePicker showTime format="YYYY-MM-DD HH:mm:ss" style={{ width: '100%' }} />
                                        </Form.Item>
                                        <Form.Item
                                            name={['user', 'description']}
                                            label="Description"
                                            style={{ margin: '0 0 10px 0' }}
                                        >
                                            <Input.TextArea />
                                        </Form.Item>
                                        <Form.Item
                                            name={['user', 'description']}
                                            style={{ margin: '0 0 10px 0' }}
                                        >
                                            <Input placeholder="Price Calculater Component" />
                                        </Form.Item>
                                        <Form.Item label="Upload Pay Slip"
                                            rules={[
                                                {
                                                    required: true,
                                                },
                                            ]}>
                                            <Upload name="logo" action="/upload.do" listType="picture" style={{ width: '100%' }}>
                                                <Button icon={<UploadOutlined />} block>
                                                    Click to upload
                                                </Button>
                                            </Upload>
                                        </Form.Item>
                                        <Form.Item wrapperCol={{ ...layout.wrapperCol }} style={{ margin: '0 0 10px 0' }}>
                                            <Button type="primary" htmlType="submit" block>
                                                Submit
                                            </Button>
                                        </Form.Item>
                                        <Form.Item wrapperCol={{ ...layout.wrapperCol }} style={{ margin: '0 0 10px 0' }}>
                                            <Button type="danger" htmlType="submit" block>
                                                Cancel
                                            </Button>
                                        </Form.Item>
                                    </Form>
                                </div>
                            </Modal>
                            <Button type="primary" onClick={showModal2} size='large' style={{ backgroundColor: '#BE1D37', border: '#BE1D37', width:'200px' }}>WRITE A REVIEW</Button>
                            <Modal title='Review Your Standee' visible={isModalVisible2} onOk={handleOk2} onCancel={handleCancel2} footer={null} width={350} style={{ paddingRight: '0' }}>
                                <div style={{ ...styleContainer }}>
                                    <Form name="nest-messages" onFinish={onFinish} validateMessages={validateMessages} style={{ width: '100%' }}>
                                        <Form.Item
                                            {...layout}
                                            name={['user', 'description']}
                                            label="Tell me something?"
                                            style={{ margin: '0 0 20px 0', }}
                                        >
                                            <Input.TextArea style={{ height: '150px' }} />
                                        </Form.Item>
                                        <Form.Item label="Rate" layout='horizontal' style={{ margin: '0 0 20px 0' }}>
                                            <Rate />
                                        </Form.Item>
                                        <Form.Item wrapperCol={{ ...layout.wrapperCol }} style={{ margin: '0 0 10px 0' }}>
                                            <Button type="primary" htmlType="submit" block>
                                                Submit
                                            </Button>
                                        </Form.Item>
                                    </Form>
                                </div>
                            </Modal>
                        </div>
                    </div>
                    <div className='comment-section'>
                        <h1 style={{ color: 'white', marginLeft: '0' }}>Review from User</h1>
                        <div className='comment-feed'>
                            <AntdComment/>
                            <AntdComment/>
                        </div>
                    </div>
                </div>
            </Content>
            </div>
            <Footer />
        </Layout>
    )
}




export default Profile;
